INSERT INTO `zt_tp_product` VALUES ('KESRyePK9u8a', null, '6', 'Plxo5q', 'fKAKiC', '1', '1', 'iPhone 8 Plus', '<div class=\"para\" label-module=\"para\" style=\"font-size: 14px; word-wrap: break-word; color: rgb(51, 51, 51); margin-bottom: 15px; text-indent: 28px; line-height: 24px; zoom: 1; font-family: arial, 宋体, sans-serif; background-color: rgb(255, 255, 255);\">iPhone 8 Plus是<a target=\"_blank\" href=\"https://baike.baidu.com/item/Apple/3860362\" data-lemmaid=\"3860362\" style=\"color: rgb(19, 110, 194); text-decoration-line: none;\">Apple</a>（<a target=\"_blank\" href=\"https://baike.baidu.com/item/%E8%8B%B9%E6%9E%9C%E5%85%AC%E5%8F%B8\" style=\"color: rgb(19, 110, 194); text-decoration-line: none;\">苹果公司</a>）第11代手机，北京时间2017年9月13日凌晨1点，在<a target=\"_blank\" href=\"https://baike.baidu.com/item/Apple%20Park\" style=\"color: rgb(19, 110, 194); text-decoration-line: none;\">Apple Park</a>新总部的<a target=\"_blank\" href=\"https://baike.baidu.com/item/%E5%8F%B2%E8%92%82%E5%A4%AB%C2%B7%E4%B9%94%E5%B8%83%E6%96%AF%E5%89%A7%E9%99%A2\" style=\"color: rgb(19, 110, 194); text-decoration-line: none;\">史蒂夫·乔布斯剧院</a>举行苹果新品发布会上发布。</div><div class=\"para\" label-module=\"para\" style=\"font-size: 14px; word-wrap: break-word; color: rgb(51, 51, 51); margin-bottom: 15px; text-indent: 28px; line-height: 24px; zoom: 1; font-family: arial, 宋体, sans-serif; background-color: rgb(255, 255, 255);\">iPhone 8 Plus改用了前后玻璃面设计，支持无线充电，拥有苹果最新的A11处理器，内存为64GB、256GB两个版本，中国大陆起售价为6688元。</div><div class=\"para\" label-module=\"para\" style=\"font-size: 14px; word-wrap: break-word; color: rgb(51, 51, 51); margin-bottom: 15px; text-indent: 28px; line-height: 24px; zoom: 1; font-family: arial, 宋体, sans-serif; background-color: rgb(255, 255, 255);\"><div class=\"para-title level-2\" label-module=\"para-title\" style=\"clear: both; zoom: 1; overflow: hidden; border-left: 12px solid rgb(79, 156, 238); line-height: 24px; font-size: 22px; font-family: \" microsoft=\"\" yahei\",=\"\" simhei,=\"\" verdana;=\"\" margin:=\"\" 35px=\"\" 0px=\"\" 15px=\"\" -30px;=\"\" background-image:=\"\" url(\"https:=\"\" bkssl.bdimg.com=\"\" static=\"\" wiki-lemma=\"\" normal=\"\" resource=\"\" img=\"\" paratitle-line_c5e6d61.png\");=\"\" background-position:=\"\" initial;=\"\" background-size:=\"\" background-repeat:=\"\" background-attachment:=\"\" background-origin:=\"\" background-clip:=\"\" position:=\"\" relative;=\"\" text-indent:=\"\" 0px;\"=\"\"><h2 class=\"title-text\" style=\"margin: 0px; padding: 0px 8px 0px 18px; font-size: 22px; color: rgb(0, 0, 0); float: left; line-height: 24px; font-weight: 400; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">外观设计</h2><br></div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\">iPhone 8 Plus为太空级别铝质设计，前后均为玻璃镜面，防水、防尘，还支持无线充电，有三款颜色可选，分别为银色、太空灰和金色。</div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\"><span style=\"color: rgb(0, 0, 0); font-size: 22px;\">产品参数</span></div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\">iPhone 8 Plus是双1200万像素，<div class=\"lemma-picture text-pic layout-right\" style=\"border: 1px solid rgb(224, 224, 224); overflow: hidden; margin: 0px 0px 3px; position: relative; float: right; clear: right; width: 220px;\"><a class=\"image-link\" nslog-type=\"9317\" href=\"https://baike.baidu.com/pic/iPhone%208%20Plus/19912821/0/fd039245d688d43f6d1b7ed5761ed21b0ef43b63?fr=lemma&amp;ct=single\" target=\"_blank\" title=\"\" style=\"color: rgb(19, 110, 194); text-decoration-line: none; display: block; width: 220px; height: 146px;\"><img class=\"\" src=\"https://gss2.bdstatic.com/9fo3dSag_xI4khGkpoWK1HF6hhy/baike/s%3D220/sign=b45057a6c01349547a1eef66664f92dd/fd039245d688d43f6d1b7ed5761ed21b0ef43b63.jpg\" alt=\"\" style=\"border: 0px; display: block; margin: 0px auto; width: 220px; height: 146px;\"></a></div>苹果强调采用面积更大、速度更快的感光元件，视频拍摄支持4K 60FPS。闪光灯加入了“慢速同步技术”，前置则完全一致的700万像素。&nbsp;iPhone 8 Plus后置双摄像头主打机器学习的人像背景虚化拍摄，支持60fps码流的4K视频拍摄。支持无线充电。还有一个特点是其图形传感器加入了对AR技术的支持。</div></div>', '', '颜色', '内存', '', '', '', null, null, '5', '6', '0.00', '0.00', '0.00', null, null, '0', null, 'Xinda/Product/2017-09-20/59c1d334755ab.jpg', '0', '腰立辉', '2017-09-20 14:55:09');
INSERT INTO `zt_tp_product` VALUES ('NbceGSVJxdVg', null, '7', 'Plxo5q', 'fKAKiC', '1', '1', 'iPhone X', '<div class=\"para\" label-module=\"para\" style=\"font-size: 14px; word-wrap: break-word; color: rgb(51, 51, 51); margin-bottom: 15px; text-indent: 28px; line-height: 24px; zoom: 1; font-family: arial, 宋体, sans-serif; background-color: rgb(255, 255, 255);\">iPhone X是<a target=\"_blank\" href=\"https://baike.baidu.com/item/Apple/3860362\" data-lemmaid=\"3860362\" style=\"color: rgb(19, 110, 194); text-decoration-line: none;\">Apple</a>（<a target=\"_blank\" href=\"https://baike.baidu.com/item/%E8%8B%B9%E6%9E%9C%E5%85%AC%E5%8F%B8\" style=\"color: rgb(19, 110, 194); text-decoration-line: none;\">苹果公司</a>）于北京时间2017年9月13日凌晨1点，在<a target=\"_blank\" href=\"https://baike.baidu.com/item/Apple%20Park\" style=\"color: rgb(19, 110, 194); text-decoration-line: none;\">Apple Park</a>新总部的<a target=\"_blank\" href=\"https://baike.baidu.com/item/%E5%8F%B2%E8%92%82%E5%A4%AB%C2%B7%E4%B9%94%E5%B8%83%E6%96%AF%E5%89%A7%E9%99%A2\" style=\"color: rgb(19, 110, 194); text-decoration-line: none;\">史蒂夫·乔布斯剧院</a>会上发布的新机型。</div><div class=\"para\" label-module=\"para\" style=\"font-size: 14px; word-wrap: break-word; color: rgb(51, 51, 51); margin-bottom: 15px; text-indent: 28px; line-height: 24px; zoom: 1; font-family: arial, 宋体, sans-serif; background-color: rgb(255, 255, 255);\">其X是罗马数字“10”的意思，代表的苹果向iPhone问世十周年致敬。</div><div class=\"para\" label-module=\"para\" style=\"font-size: 14px; word-wrap: break-word; color: rgb(51, 51, 51); margin-bottom: 15px; text-indent: 28px; line-height: 24px; zoom: 1; font-family: arial, 宋体, sans-serif; background-color: rgb(255, 255, 255);\">iPhone X属于高端版机型，采用全新设计，搭载色彩锐利的OLED屏幕，配备升级后的相机，使用3D面部识别传感器解锁手机，支持AirPower无线充电</div><div class=\"para\" label-module=\"para\" style=\"font-size: 14px; word-wrap: break-word; color: rgb(51, 51, 51); margin-bottom: 15px; text-indent: 28px; line-height: 24px; zoom: 1; font-family: arial, 宋体, sans-serif; background-color: rgb(255, 255, 255);\">分为64GB、256GB两个版本，起售价999美元，2017年10月27日预售。</div><div class=\"para\" label-module=\"para\" style=\"font-size: 14px; word-wrap: break-word; color: rgb(51, 51, 51); margin-bottom: 15px; text-indent: 28px; line-height: 24px; zoom: 1; font-family: arial, 宋体, sans-serif; background-color: rgb(255, 255, 255);\"><div class=\"para-title level-2\" label-module=\"para-title\" style=\"clear: both; zoom: 1; overflow: hidden; border-left: 12px solid rgb(79, 156, 238); line-height: 24px; font-size: 22px; font-family: &quot;Microsoft YaHei&quot;, SimHei, Verdana; margin: 35px 0px 15px -30px; background-image: url(&quot;https://bkssl.bdimg.com/static/wiki-lemma/normal/resource/img/paraTitle-line_c5e6d61.png&quot;); background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; position: relative; text-indent: 0px;\"><h2 class=\"title-text\" style=\"margin: 0px; padding: 0px 8px 0px 18px; font-size: 22px; color: rgb(0, 0, 0); float: left; line-height: 24px; font-weight: 400; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">外观特点</h2><br></div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\">“整部<a target=\"_blank\" href=\"https://baike.baidu.com/item/iPhone%20X\" style=\"color: rgb(19, 110, 194); text-decoration-line: none;\">iPhone X</a>，看起来就是一块几乎整面的屏幕。”苹果中国官网是这<div class=\"lemma-picture text-pic layout-right\" style=\"border: 1px solid rgb(224, 224, 224); overflow: hidden; margin: 0px 0px 3px; position: relative; float: right; clear: right; width: 110px;\"><a class=\"image-link\" nslog-type=\"9317\" href=\"https://baike.baidu.com/pic/iPhone%20X/22117423/0/574e9258d109b3de35462378c7bf6c81800a4c21?fr=lemma&amp;ct=single\" target=\"_blank\" title=\"iPhone X\" style=\"color: rgb(19, 110, 194); text-decoration-line: none; display: block; width: 110px; height: 220px;\"><img class=\"\" src=\"https://gss1.bdstatic.com/9vo3dSag_xI4khGkpoWK1HF6hhy/baike/s%3D220/sign=dfcdb916a7af2eddd0f14eebbd110102/574e9258d109b3de35462378c7bf6c81800a4c21.jpg\" alt=\"iPhone X\" style=\"border: 0px; display: block; margin: 0px auto; width: 110px; height: 220px;\"></a><span class=\"description\" style=\"display: block; color: rgb(85, 85, 85); font-size: 12px; text-indent: 0px; font-family: 宋体; word-wrap: break-word; word-break: break-all; line-height: 15px; padding: 8px 7px; min-height: 12px; border-top: 1px solid rgb(224, 224, 224);\">iPhone X</span></div>么来形容iPhone X这次所采用的全面屏设计。iPhone X<span style=\"font-size: 10.5px; line-height: 0; position: relative; vertical-align: baseline; top: -0.5em; white-space: nowrap; margin-left: 2px; color: rgb(51, 102, 204); cursor: default; padding: 0px 2px;\">[1]</span><a class=\"sup-anchor\" name=\"ref_[1]_22758392\" style=\"color: rgb(19, 110, 194); position: relative; top: -50px; font-size: 0px; line-height: 0;\">&nbsp;</a>&nbsp;取消了苹果沿用了10年的底部<a target=\"_blank\" href=\"https://baike.baidu.com/item/Home%E9%94%AE\" style=\"color: rgb(19, 110, 194); text-decoration-line: none;\">Home键</a>，采用双面玻璃+金属材质中框设计，材质为OLED，采用了防油渍防指纹涂层，底部的接口和两侧的按键设计和iPhone第七代系列一样，没有变化。</div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\">背部的明显变化是后置双摄改为了竖向排列。</div><div class=\"anchor-list\" style=\"position: relative; font-size: 12px; text-indent: 0px;\"><a name=\"2\" class=\"lemma-anchor para-title\" style=\"color: rgb(19, 110, 194); position: absolute; top: -50px;\"></a><a name=\"sub22758392_2\" class=\"lemma-anchor \" style=\"color: rgb(19, 110, 194); position: absolute; top: -50px;\"></a><a name=\"功能配置\" class=\"lemma-anchor \" style=\"color: rgb(19, 110, 194); position: absolute; top: -50px;\"></a></div><div class=\"para-title level-2\" label-module=\"para-title\" style=\"clear: both; zoom: 1; overflow: hidden; border-left: 12px solid rgb(79, 156, 238); line-height: 24px; font-size: 22px; font-family: &quot;Microsoft YaHei&quot;, SimHei, Verdana; margin: 35px 0px 15px -30px; background-image: url(&quot;https://bkssl.bdimg.com/static/wiki-lemma/normal/resource/img/paraTitle-line_c5e6d61.png&quot;); background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; position: relative; text-indent: 0px;\"><h2 class=\"title-text\" style=\"margin: 0px; padding: 0px 8px 0px 18px; font-size: 22px; color: rgb(0, 0, 0); float: left; line-height: 24px; font-weight: 400; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">功能配置</h2><br></div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\">iPhone X显示屏是iPhone有史以来最为出色一块屏幕，支持3<div class=\"lemma-picture text-pic layout-right\" style=\"border: 1px solid rgb(224, 224, 224); overflow: hidden; margin: 0px 0px 3px; position: relative; float: right; clear: right; width: 250px;\"><a class=\"image-link\" nslog-type=\"9317\" href=\"https://baike.baidu.com/pic/iPhone%20X/22117423/0/58ee3d6d55fbb2fb7acd9f02444a20a44623dc38?fr=lemma&amp;ct=single\" target=\"_blank\" title=\"传感器\" style=\"color: rgb(19, 110, 194); text-decoration-line: none; display: block; width: 250px; height: 111px;\"><img class=\"\" src=\"https://gss1.bdstatic.com/9vo3dSag_xI4khGkpoWK1HF6hhy/baike/s%3D250/sign=734b0423788da9774a2f812e8050f872/58ee3d6d55fbb2fb7acd9f02444a20a44623dc38.jpg\" alt=\"传感器\" style=\"border: 0px; display: block; margin: 0px auto; width: 250px; height: 111px;\"></a><span class=\"description\" style=\"display: block; color: rgb(85, 85, 85); font-size: 12px; text-indent: 0px; font-family: 宋体; word-wrap: break-word; word-break: break-all; line-height: 15px; padding: 8px 7px; min-height: 12px; border-top: 1px solid rgb(224, 224, 224);\">传感器</span></div>D Touch压感操作，支持HDR显示。iPhone X使用<a target=\"_blank\" href=\"https://baike.baidu.com/item/Face%20ID\" style=\"color: rgb(19, 110, 194); text-decoration-line: none;\">Face ID</a>人脸识别。额头处除了传统的光线与距离传感器，集成了红外线等传感器，其前置镜头也提升到了700万像素，支持背景虚化自拍。iPhone X加入了无线充电功能，支持IP67级别防水防尘。</div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\">iPhone X搭载的A11 Bionic处理器加入人工智能神经网络和AR方面的支持，搭载了提升拍照的苹果独立设计的ISP。截止发布iPhone X前苹果性能最为强劲的一款处理器。</div><div class=\"anchor-list\" style=\"position: relative; font-size: 12px; text-indent: 0px;\"><a name=\"3\" class=\"lemma-anchor para-title\" style=\"color: rgb(19, 110, 194); position: absolute; top: -50px;\"></a><a name=\"sub22758392_3\" class=\"lemma-anchor \" style=\"color: rgb(19, 110, 194); position: absolute; top: -50px;\"></a><a name=\"规格参数\" class=\"lemma-anchor \" style=\"color: rgb(19, 110, 194); position: absolute; top: -50px;\"></a></div><div class=\"para-title level-2\" label-module=\"para-title\" style=\"clear: both; zoom: 1; overflow: hidden; border-left: 12px solid rgb(79, 156, 238); line-height: 24px; font-size: 22px; font-family: &quot;Microsoft YaHei&quot;, SimHei, Verdana; margin: 35px 0px 15px -30px; background-image: url(&quot;https://bkssl.bdimg.com/static/wiki-lemma/normal/resource/img/paraTitle-line_c5e6d61.png&quot;); background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; position: relative; text-indent: 0px;\"><h2 class=\"title-text\" style=\"margin: 0px; padding: 0px 8px 0px 18px; font-size: 22px; color: rgb(0, 0, 0); float: left; line-height: 24px; font-weight: 400; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">规格参数</h2><br></div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\">屏幕尺寸5.8英寸，重量174克，高度143.6mm，宽度和厚度分别为70.9mm、7.7mm，2436 * 1125 像素分辨率，458 ppi。</div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\">背部后置双摄，为1200万像素广角镜头（F/1.8光圈）+1200万像素长焦镜头（F/2.4光圈）组合，支持人像模式背景虚化。</div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\">处理器：A11 Bionic采用6核64位架构，集成M11运动协处理器，A11比A10运行速度提升70%，苹果自主研发的GPU性能则提升30%</div><div class=\"anchor-list\" style=\"position: relative; font-size: 12px; text-indent: 0px;\"><a name=\"4\" class=\"lemma-anchor para-title\" style=\"color: rgb(19, 110, 194); position: absolute; top: -50px;\"></a><a name=\"sub22758392_4\" class=\"lemma-anchor \" style=\"color: rgb(19, 110, 194); position: absolute; top: -50px;\"></a><a name=\"主要功能\" class=\"lemma-anchor \" style=\"color: rgb(19, 110, 194); position: absolute; top: -50px;\"></a></div><div class=\"para-title level-2\" label-module=\"para-title\" style=\"clear: both; zoom: 1; overflow: hidden; border-left: 12px solid rgb(79, 156, 238); line-height: 24px; font-size: 22px; font-family: &quot;Microsoft YaHei&quot;, SimHei, Verdana; margin: 35px 0px 15px -30px; background-image: url(&quot;https://bkssl.bdimg.com/static/wiki-lemma/normal/resource/img/paraTitle-line_c5e6d61.png&quot;); background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; position: relative; text-indent: 0px;\"><h2 class=\"title-text\" style=\"margin: 0px; padding: 0px 8px 0px 18px; font-size: 22px; color: rgb(0, 0, 0); float: left; line-height: 24px; font-weight: 400; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">主要功能</h2><br></div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\"><span style=\"font-weight: 700;\">全面屏，前后玻璃</span></div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\">iPhoneX采用无边框屏幕，采用超级retina显示屏以及OLED技术。从外观上来看，正面由一块异形屏构成，整个前部只保留顶部的耳机、自拍相机和传感器。</div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\">iPhoneX尺寸是5.8英寸，外框将是一个连续的不锈钢带，前面和后面都是增强性玻璃面。这是iPhone自2010年发布iPhone4之后，又重新回归前后玻璃+金属中框设计。<span style=\"font-size: 10.5px; line-height: 0; position: relative; vertical-align: baseline; top: -0.5em; white-space: nowrap; margin-left: 2px; color: rgb(51, 102, 204); cursor: default; padding: 0px 2px;\">[2]</span><a class=\"sup-anchor\" name=\"ref_[2]_22758392\" style=\"color: rgb(19, 110, 194); position: relative; top: -50px; font-size: 0px; line-height: 0;\">&nbsp;</a></div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\"><span style=\"font-weight: 700;\">后置竖排双摄，玻璃后壳</span></div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\">iPhoneX采用了后置双1200万像素摄像头，广角镜头光圈为F1.8、长焦镜头光圈升级至F2.4，双镜头均支持光学防抖，并且采用了四颗LED补光灯。零延迟的快门，可以更加准确抓住关键瞬间。前置摄像头700万像素，光圈F2.2，拥有景深识别功能，可以拍摄人像模式的自拍。</div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\">拥有比以往尺寸更大、速度更快的1200万像素感光元件、新的色彩滤镜、更深层的像素，以及新的支持光学图像防抖的长焦镜头。两个后置镜头都具有光学图像防抖功能，而且反应飞快，即使在弱光下，也能拍出效果出众的照片和视频。</div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\">iPhone X能通过广角和长焦镜头实现光学变焦。另外，通过数码变焦，还能拍摄最高达10倍变焦的照片和最高达6倍变焦的视频。</div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\">原深感摄像头能对50多种不同的面部肌肉运动进行分析，从而以12款“动话表情”镜像出你的神态。</div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\">Apple设计的新款三核心图形处理器，与A10 Fusion相比，速度提升幅度最高可达30% 之多。<span style=\"font-size: 10.5px; line-height: 0; position: relative; vertical-align: baseline; top: -0.5em; white-space: nowrap; margin-left: 2px; color: rgb(51, 102, 204); cursor: default; padding: 0px 2px;\">[2]</span><a class=\"sup-anchor\" name=\"ref_[2]_22758392\" style=\"color: rgb(19, 110, 194); position: relative; top: -50px; font-size: 0px; line-height: 0;\">&nbsp;</a></div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\"><span style=\"font-weight: 700;\">FaceID</span></div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\">配合A11BIONIC仿生芯片。<span style=\"font-size: 10.5px; line-height: 0; position: relative; vertical-align: baseline; top: -0.5em; white-space: nowrap; margin-left: 2px; color: rgb(51, 102, 204); cursor: default; padding: 0px 2px;\">[2]</span><a class=\"sup-anchor\" name=\"ref_[2]_22758392\" style=\"color: rgb(19, 110, 194); position: relative; top: -50px; font-size: 0px; line-height: 0;\">&nbsp;</a></div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\">从滑动解锁到touchID，再到面部识别解锁，iPhoneX再一次创新了手机解锁方式。iPhoneX取消了Home键，而原本集合在Home键上的touchID功能将被FaceID取代。该功能可以支持用户面部解锁。据介绍，为了实现FaceID，苹果运用了许多最先进的技术：红外摄像头、环境感应器、光感传感器等。<span style=\"font-size: 10.5px; line-height: 0; position: relative; vertical-align: baseline; top: -0.5em; white-space: nowrap; margin-left: 2px; color: rgb(51, 102, 204); cursor: default; padding: 0px 2px;\">[2]</span><a class=\"sup-anchor\" name=\"ref_[2]_22758392\" style=\"color: rgb(19, 110, 194); position: relative; top: -50px; font-size: 0px; line-height: 0;\">&nbsp;</a></div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\">在安全性方面，TouchID的识别准确度是五万分之一，但FaceID解锁iphone的准确度是100万分之一。换句话说，另一个人用FaceID解锁你的iPhone的几率是百万分之一。同时，TouchID还能够实现“刷脸”支付。<span style=\"font-size: 10.5px; line-height: 0; position: relative; vertical-align: baseline; top: -0.5em; white-space: nowrap; margin-left: 2px; color: rgb(51, 102, 204); cursor: default; padding: 0px 2px;\">[2]</span><a class=\"sup-anchor\" name=\"ref_[2]_22758392\" style=\"color: rgb(19, 110, 194); position: relative; top: -50px; font-size: 0px; line-height: 0;\">&nbsp;</a></div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\"><span style=\"font-weight: 700;\">无线充电</span></div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\">iPhoneX支持无线充电，并兼容Qi充电器。iPhone，AppleWatch与AirPods均可以放在一块无线充电器上同时充电，官方称AirPower。<span style=\"font-size: 10.5px; line-height: 0; position: relative; vertical-align: baseline; top: -0.5em; white-space: nowrap; margin-left: 2px; color: rgb(51, 102, 204); cursor: default; padding: 0px 2px;\">[2]</span><a class=\"sup-anchor\" name=\"ref_[2]_22758392\" style=\"color: rgb(19, 110, 194); position: relative; top: -50px; font-size: 0px; line-height: 0;\">&nbsp;</a></div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\">苹果此次发布的三款手机iPhone8、iPhone8Plus和iPhoneX均支持无线充电，这也是苹果首次为iPhone提供该功能。并且有意思的是，苹果将该功能命名为“气”，似乎引用了中国功夫中隔空传递的一个概念。<span style=\"font-size: 10.5px; line-height: 0; position: relative; vertical-align: baseline; top: -0.5em; white-space: nowrap; margin-left: 2px; color: rgb(51, 102, 204); cursor: default; padding: 0px 2px;\">[2]</span><a class=\"sup-anchor\" name=\"ref_[2]_22758392\" style=\"color: rgb(19, 110, 194); position: relative; top: -50px; font-size: 0px; line-height: 0;\">&nbsp;</a></div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\"><span style=\"font-weight: 700;\">取消Home键</span></div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\">iPhoneX取消了home键，只用触摸屏幕，即可唤醒。<span style=\"font-size: 10.5px; line-height: 0; position: relative; vertical-align: baseline; top: -0.5em; white-space: nowrap; margin-left: 2px; color: rgb(51, 102, 204); cursor: default; padding: 0px 2px;\">[2]</span><a class=\"sup-anchor\" name=\"ref_[2]_22758392\" style=\"color: rgb(19, 110, 194); position: relative; top: -50px; font-size: 0px; line-height: 0;\">&nbsp;</a></div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\"><span style=\"font-weight: 700;\">A11Fusion处理器</span></div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\">A<div class=\"lemma-picture text-pic layout-right\" style=\"border: 1px solid rgb(224, 224, 224); overflow: hidden; margin: 0px 0px 3px; position: relative; float: right; clear: right; width: 220px;\"><a class=\"image-link\" nslog-type=\"9317\" href=\"https://baike.baidu.com/pic/iPhone%20X/22117423/0/6609c93d70cf3bc714a1d2fdda00baa1cd112a79?fr=lemma&amp;ct=single\" target=\"_blank\" title=\"苹果A11 Bionic处理器\" style=\"color: rgb(19, 110, 194); text-decoration-line: none; display: block; width: 220px; height: 183px;\"><img class=\"\" src=\"https://gss3.bdstatic.com/-Po3dSag_xI4khGkpoWK1HF6hhy/baike/s%3D220/sign=168ad7dba451f3dec7b2be66a4eff0ec/6609c93d70cf3bc714a1d2fdda00baa1cd112a79.jpg\" alt=\"苹果A11 Bionic处理器\" style=\"border: 0px; display: block; margin: 0px auto; width: 220px; height: 183px;\"></a><span class=\"description\" style=\"display: block; color: rgb(85, 85, 85); font-size: 12px; text-indent: 0px; font-family: 宋体; word-wrap: break-word; word-break: break-all; line-height: 15px; padding: 8px 7px; min-height: 12px; border-top: 1px solid rgb(224, 224, 224);\">苹果A11 Bionic处理器</span></div>11仿生闪亮登场。这款iPhone上有史以来最强大、最智能的芯片，拥有一个每秒运算次数最高可达6000亿次的神经网络引擎。<span style=\"font-size: 10.5px; line-height: 0; position: relative; vertical-align: baseline; top: -0.5em; white-space: nowrap; margin-left: 2px; color: rgb(51, 102, 204); cursor: default; padding: 0px 2px;\">[2]</span><a class=\"sup-anchor\" name=\"ref_[2]_22758392\" style=\"color: rgb(19, 110, 194); position: relative; top: -50px; font-size: 0px; line-height: 0;\">&nbsp;</a></div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\">全新中央处理器的四个能效核心速度比A10Fusion最高提升70%，两个性能核心也有了最高达25%的速度提升。<span style=\"font-size: 10.5px; line-height: 0; position: relative; vertical-align: baseline; top: -0.5em; white-space: nowrap; margin-left: 2px; color: rgb(51, 102, 204); cursor: default; padding: 0px 2px;\">[2]</span><a class=\"sup-anchor\" name=\"ref_[2]_22758392\" style=\"color: rgb(19, 110, 194); position: relative; top: -50px; font-size: 0px; line-height: 0;\">&nbsp;</a></div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\">利用机器学习技术，当你的样貌随着时间推移发生了改变，面容ID也会跟着调整适应。<span style=\"font-size: 10.5px; line-height: 0; position: relative; vertical-align: baseline; top: -0.5em; white-space: nowrap; margin-left: 2px; color: rgb(51, 102, 204); cursor: default; padding: 0px 2px;\">[2]</span><a class=\"sup-anchor\" name=\"ref_[2]_22758392\" style=\"color: rgb(19, 110, 194); position: relative; top: -50px; font-size: 0px; line-height: 0;\">&nbsp;</a></div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\"><span style=\"font-weight: 700;\">AR</span></div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\">iPhoneX的后置摄像头区域还加入了3D激光测距系统，HomePod的固件中有与新的“SmartCam”模式相关的配置，它能够检测用户周围的环境，据此调整曝光、焦点和白平衡等。<span style=\"font-size: 10.5px; line-height: 0; position: relative; vertical-align: baseline; top: -0.5em; white-space: nowrap; margin-left: 2px; color: rgb(51, 102, 204); cursor: default; padding: 0px 2px;\">[2]</span><a class=\"sup-anchor\" name=\"ref_[2]_22758392\" style=\"color: rgb(19, 110, 194); position: relative; top: -50px; font-size: 0px; line-height: 0;\">&nbsp;</a></div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\"><span style=\"font-weight: 700;\">取消Home键</span></div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\">因为iPhoneX采用全面屏设计，所以实体Home键被取消了，不过全新的iPhoneX带来了全新的操作体验，可以通过语音、按侧边按键启动Siri。<span style=\"font-size: 10.5px; line-height: 0; position: relative; vertical-align: baseline; top: -0.5em; white-space: nowrap; margin-left: 2px; color: rgb(51, 102, 204); cursor: default; padding: 0px 2px;\">[2]</span><a class=\"sup-anchor\" name=\"ref_[2]_22758392\" style=\"color: rgb(19, 110, 194); position: relative; top: -50px; font-size: 0px; line-height: 0;\">&nbsp;</a></div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\"><span style=\"font-weight: 700;\">Animoji：可记录你表情的动态emoji</span></div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\">有了先进的面部识别技术，因此iPhoneX将原本静态的emoji表情升级为动态Animoji。Animoji表情可以“追随”用户的表情，形成动态的、个性化的表情包。该功能可在信息应用中创建，目前仅提供有限的几种，后续可能会陆续增加数量。<span style=\"font-size: 10.5px; line-height: 0; position: relative; vertical-align: baseline; top: -0.5em; white-space: nowrap; margin-left: 2px; color: rgb(51, 102, 204); cursor: default; padding: 0px 2px;\">[2]</span><a class=\"sup-anchor\" name=\"ref_[2]_22758392\" style=\"color: rgb(19, 110, 194); position: relative; top: -50px; font-size: 0px; line-height: 0;\">&nbsp;</a></div><div class=\"anchor-list\" style=\"position: relative; font-size: 12px; text-indent: 0px;\"><a name=\"5\" class=\"lemma-anchor para-title\" style=\"color: rgb(19, 110, 194); position: absolute; top: -50px;\"></a><a name=\"sub22758392_5\" class=\"lemma-anchor \" style=\"color: rgb(19, 110, 194); position: absolute; top: -50px;\"></a><a name=\"产品发布\" class=\"lemma-anchor \" style=\"color: rgb(19, 110, 194); position: absolute; top: -50px;\"></a></div><div class=\"para-title level-2\" label-module=\"para-title\" style=\"clear: both; zoom: 1; overflow: hidden; border-left: 12px solid rgb(79, 156, 238); line-height: 24px; font-size: 22px; font-family: &quot;Microsoft YaHei&quot;, SimHei, Verdana; margin: 35px 0px 15px -30px; background-image: url(&quot;https://bkssl.bdimg.com/static/wiki-lemma/normal/resource/img/paraTitle-line_c5e6d61.png&quot;); background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; position: relative; text-indent: 0px;\"><h2 class=\"title-text\" style=\"margin: 0px; padding: 0px 8px 0px 18px; font-size: 22px; color: rgb(0, 0, 0); float: left; line-height: 24px; font-weight: 400; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">产品发布</h2><br></div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\">iP<div class=\"lemma-picture text-pic layout-right\" style=\"border: 1px solid rgb(224, 224, 224); overflow: hidden; margin: 0px 0px 3px; position: relative; float: right; clear: right; width: 220px;\"><a class=\"image-link\" nslog-type=\"9317\" href=\"https://baike.baidu.com/pic/iPhone%20X/22117423/0/c2cec3fdfc039245bfd98d238c94a4c27d1e25a9?fr=lemma&amp;ct=single\" target=\"_blank\" title=\"iPhone X\" style=\"color: rgb(19, 110, 194); text-decoration-line: none; display: block; width: 220px; height: 123px;\"><img class=\"\" src=\"https://gss1.bdstatic.com/9vo3dSag_xI4khGkpoWK1HF6hhy/baike/s%3D220/sign=35316db455df8db1b82e7b663923dddb/c2cec3fdfc039245bfd98d238c94a4c27d1e25a9.jpg\" alt=\"iPhone X\" style=\"border: 0px; display: block; margin: 0px auto; width: 220px; height: 123px;\"></a><span class=\"description\" style=\"display: block; color: rgb(85, 85, 85); font-size: 12px; text-indent: 0px; font-family: 宋体; word-wrap: break-word; word-break: break-all; line-height: 15px; padding: 8px 7px; min-height: 12px; border-top: 1px solid rgb(224, 224, 224);\">iPhone X</span></div>hone X分为64GB和256GB两个版本，美版不含税的价格为999美元起售（约合人民币6527元）。国行版价格分别为8388元与9688元，分为银色和灰色两种颜色，为史上最贵的<a target=\"_blank\" href=\"https://baike.baidu.com/item/iPhone\" style=\"color: rgb(19, 110, 194); text-decoration-line: none;\">iPhone</a>手机。</div><div class=\"para\" label-module=\"para\" style=\"word-wrap: break-word; margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1;\">iPhone X上市时间要晚于iPhone 8系列，2017年10月27日下午3：01开启预约，2017年11月3日正式开卖。<span style=\"font-size: 10.5px; line-height: 0; position: relative; vertical-align: baseline; top: -0.5em; white-space: nowrap; margin-left: 2px; color: rgb(51, 102, 204); cursor: default; padding: 0px 2px;\">[4-5]</span><a class=\"sup-anchor\" name=\"ref_[4-5]_22758392\" style=\"color: rgb(19, 110, 194); position: relative; top: -50px; font-size: 0px; line-height: 0;\">&nbsp;</a></div></div><div class=\"para\" label-module=\"para\" style=\"font-size: 14px; word-wrap: break-word; color: rgb(51, 51, 51); margin-bottom: 15px; text-indent: 28px; line-height: 24px; zoom: 1; font-family: arial, 宋体, sans-serif; background-color: rgb(255, 255, 255);\"><br></div>', '', '颜色', '内存', '', '', '', null, null, '5', '6', '0.00', '0.00', '0.00', null, null, '0', null, 'Xinda/Product/2017-09-20/59c1d37a5817e.jpg', '0', '腰立辉', '2017-09-20 14:26:51');
